@media (max-width:900px){.site-header__container{width:100%;padding-inline:clamp(1.3rem,4vw,2rem)}main{width:100%;}.page-wrapper{width:100%;padding-inline:clamp(1.4rem,5vw,2.3rem)}.site-header .mobile-panel{inset-inline:0;left:0;right:0;width:100vw;border-radius:0 0 1.5rem 1.5rem;margin-inline:auto}}
@media (max-width:640px){.hero-alert{flex-direction:column;align-items:flex-start}.back-to-top{right:clamp(1.1rem,4vw,1.6rem);bottom:clamp(1.2rem,5vw,1.8rem)}.gallery-card .image-grid{grid-template-columns:1fr}.page-section__card{gap:1.4rem}}

/* Mobile polish additions */
:root{
  --space-1:0.5rem;
  --space-2:0.75rem;
  --space-3:1rem;
  --space-4:1.5rem;
  --space-5:2rem;
  --space-6:3rem;
  --content-max:1260px;
  --measure:70ch;
}

.content-shell{
  width:min(var(--content-max),92vw);
  margin-inline:auto;
}

.site-header__container{
  width:min(var(--content-max),92vw);
}

main{
  width:min(var(--content-max),92vw);
}

.page-wrapper{
  width:min(var(--content-max),92vw);
}

.readable{
  max-width:var(--measure);
}

img{
  max-width:100%;
  height:auto;
}

picture img{
  display:block;
}

.cookie-banner{
  padding-bottom:calc(1.4rem + env(safe-area-inset-bottom));
}

.no-js-nav{
  margin-top:1.5rem;
  padding:1rem 1.2rem;
  border:1px solid rgba(148,163,184,0.3);
  border-radius:1rem;
  background:#ffffffd9;
}

.no-js-nav a{
  display:inline-flex;
  margin-right:0.9rem;
  margin-bottom:0.5rem;
  text-decoration:none;
}

@media (max-width:640px){
  .no-js-nav a{
    width:100%;
  }
}

.cookie-banner__content{
  display:grid;
  gap:0.75rem;
}

.cookie-preferences{
  border:1px solid rgba(148,163,184,0.3);
  border-radius:0.75rem;
  padding:0.75rem 0.9rem;
  background:#ffffffc7;
}

.dark .cookie-preferences{
  background:#0f172acc;
  border-color:#47556999;
}

.cookie-toggle{
  display:flex;
  align-items:center;
  gap:0.5rem;
  font-weight:600;
}

.cookie-toggle input{
  width:1.1rem;
  height:1.1rem;
}

.cookie-hint{
  margin:0.4rem 0 0;
  font-size:0.9rem;
  color:var(--text-muted);
}

.hero__text{
  max-width:var(--measure);
}

.email-link,
a[href^="tel:"]{
  display:inline-flex;
  align-items:center;
  padding:0.2rem 0.35rem;
  border-radius:0.4rem;
}

.page-wrapper{
  overflow-wrap:anywhere;
}

.feature-grid{
  align-items:start;
  grid-auto-rows:1fr;
}

