        );

        const fallbackUpdate = () => {
          const point = midPoint();
          for (const section of this.sectionsList) {
            const rect = section.getBoundingClientRect();
            if (rect.top <= point && rect.bottom >= point) {
              this.activeSection = section.dataset.section;
              return;
            }
